} //--------------------------------------------------------------------------------------------------
// Public Code
//-------------------------------------------------------------------------------------------------- impl<T> MMIODerefWrapper<T> { /// Create an instance. pub const unsafe fn new(start_addr: usize) -> Self { Self { start_addr, phantom: PhantomData, } }
} // 为 `MMIODerefWrapper<T>` 实现 Deref ，作为智能指针使用，返回一个引用
impl<T> ops::Deref for MMIODerefWrapper<T> { type Target = T; fn deref(&self) -> &Self::Target { unsafe { &*(self.start_addr as *const _) } }
